function Contact() {
  return (
    <>
      <div className="contactDiv">
        <p><b>I love to hear more from you</b></p>
        <div className="contactGrid">
          <div className="contactInfo">
            <div><Globe /> <span style={{'paddingLeft': '10px'}}>miguelmanabo4@gmail.com</span></div>
            <div><Telephone /><span style={{'paddingLeft': '10px'}}>0999 888 7777</span></div>
            <div><Facebook /><span style={{'paddingLeft': '10px'}}>m.me/Archivist.Michael</span></div>
          </div>
          <div className="lineCenter">
            <div className="verticalLine"></div>
          </div>
          <div className="border">
            <form className="contactForm">
              <div>
                {/* <label htmlFor="name">Name:</label> */}
                <input className="blur" style={{'height': '30px', 'width': '400px', 'padding-left': '10px', 'border': 'rgba(255, 255, 255, 0.421)'}} type="text" id="name" name="name" placeholder="Enter Name"/>
              </div>
              <div>
                {/* <label htmlFor="email">Email:</label> */}
                <input className="blur" style={{'height': '30px', 'width': '400px', 'padding-left': '10px', 'border': 'rgba(255, 255, 255, 0.421)'}} type="email" id="email" name="email" placeholder="Enter Email"/>
              </div>
              <div>
                {/* <label htmlFor="response">Response:</label> */}
                <textarea className="blur" style={{'height': '90px', 'width': '400px', 'padding': '10px 0px 0px 10px', 'border': 'rgba(255, 255, 255, 0.421)'}} id="response" name="response" rows="4" placeholder="Your response here..."></textarea>
              </div>
              <button type="submit" style={{'height': '30px', 'width': '70px', 'borderRadius': '10px', 'border': 'rgba(255, 255, 255, 0.421)'}}>Submit</button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default Contact;
